public class MilkChocolate extends Sweet {

	public MilkChocolate() {
		// TODO Auto-generated constructor stub
		setSweetness(0);
		setWeight(0);
	}
	public MilkChocolate(double weight,double sweetness) {
		setSweetness(sweetness);
		setWeight(weight);
	}

}
